const COLORS = ["red", "blue", "green", "yellow"]

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

class App extends Component {
    state = {
      tails: [200],
  }

  generateForm() {
    const result = []
    const size = 10 + 200
    const max = 5
    var form = 0
    while (result.length < size && form < max)
    {
      form++;
      result.push(COLORS[getRandomInt(5)])
    }
    return shuffle(result)
  }

  render() {
    const { tails } = this.state
    return (
      <div className="area">
        {tails.map((color, index) => (
          <Tail
            index={index}
            color={color}
            />
        ))}
      </div>
    )
  }
}

export default App
