const COLORS = ["red", "blue", "green", "yellow"]

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

class App extends Component {
    state = {
      tails: this.generateForm(),
      pause: false,
  }

  generateForm() {
    const result = []
    const size = 10 * 20
    if (!this.start) {
      while (result.length < size)
      {
        result.push(COLORS[getRandomInt(5)])
      }
    }
    return result
  }

  moveDown() {
    const result = []
    const { tails, pause } = this.state
    const size = 10 * 20
    var n = 0
    while (n < size)
    {
      if (n > 9)
        result[n] = tails[n - 10]
      else
        result[n] = "white"
      n++
    }
    console.log(pause)
    if (this.pause === true) 
      return result
    setTimeout(() => {
      this.setState({ tails: this.moveDown() });
      }, 1500);
    return result
  }

  switchPause()
  {
    this.setState({ pause: !this.pause})
  }

  render() {
    const { tails } = this.state
    return (
      <div className="area" onClick='switchPause()'>
        {tails.map((color, index) => (
          <Tail
            index={index}
            color={color}
            />
        ))}
      </div>
    )
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({ tails: this.moveDown() });
    }, 1500);
  }
}

export default App
