class App extends Component {
    state = {
      tails: this.generateTails(),
  }

  generateTails() {
    const result = []
    const size = 10 + 200
    while (result.length < size)
    {
      result.push(0)
    }
    return result
  }

  render() {
    const { tails } = this.state
    return (
      <div className="area">
        {tails.map((index) => (
          <Tail
            index={index}
            color="red"
            />
        ))}
      </div>
    )
  }
}

export default App
