const COLORS = ["red", "blue", "green", "yellow"]

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

class App extends Component {
    state = {
      tails: this.generateForm(),
  }

  generateForm() {
    const result = []
    const size = 10 + 200
    while (result.length < size)
    {
      result.push(COLORS[getRandomInt(5)])
    }
    setTimeout(() => this.setState({ tails: this.moveDown() }), 750)
    return result
  }

  moveDown() {
    const result = []
    const size = 10 + 200
    var n = this.tails.length - 1
    while (n > 9)
    {
      result[n] = this.tails[n - 10]
      n--
    }
    while (n >= 0)
    {
      result[n] = "white"
      n--
    }
    return result
  }

  render() {
    const { tails } = this.state
    return (
      <div className="area">
        {tails.map((color, index) => (
          <Tail
            index={index}
            color={color}
            />
        ))}
      </div>
    )
  }
}

export default App
