// export default App

const COLORS = ["red", "blue", "green", "yellow"]

function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}

class App extends Component {
    state = {
      tails: this.generateForm(),
      start: false
  }

  generateForm() {
    const result = []
    const size = 10 * 20
    if (!this.start) {
      while (result.length < size)
      {
        result.push(COLORS[getRandomInt(5)])
      }
    }
    setTimeout(() => this.setState({ tails: this.moveDown(), start: true }), 750)
    return result
  }

  moveDown() {
    const result = []
    const size = 10 * 20
    var n = 0
    while (n < size)
    {
      if (n > 10)
        result[n] = this.tails[n]
      else
        result[n] = "white"
      n++
    }
    return result
  }

  render() {
    const { tails } = this.state
    return (
      <div className="area">
        {tails.map((color, index) => (
          <Tail
            index={index}
            color={color}
            />
        ))}
      </div>
    )
  }
}

export default App
